import mockApi from "./api/mockApi";
import "./App.css";
import { useState } from "react";
import { BeatLoader } from "react-spinners";

function App() {
  // const getProductsThen = () => {
  //   // this is getting the base URL and adding /product behind
  //   // "mockApi.get("/product")" will get a promise, which is a future data
  //   //".then" will consume the promise to get the response data
  //   mockApi
  //     .get("/product")
  //     .then((response) => {
  //       console.log("âœ… loaded products successfully");
  //       console.log("âœ… response", response);
  //     })
  //     // axios will throw errors depending on the response of the server
  //     .catch((error) => {
  //       console.log("âŒ error", error);
  //     })
  //     // .finally will be run be it successful or error
  //     .finally(() => {
  //       console.log("ðŸ‘ completed");
  //     });
  // };

  // set a state to tell user is the app is loading or not
  const [isloading, setisLoading] = useState(false);

  const getProducts = async () => {
    // use await to consume promise
    // await can only be use at top level and with async
    // use try-catch-finally block to catch the errors. if there's error in try block, it will jump straight to catch error block, the remaining code in try block will never run
    try {
      setisLoading(true);
      const response = await mockApi.get("/product");
      console.log("âœ… loaded products successfully");
      console.log("âœ… response", response);
    } catch (error) {
      console.log("âŒ error", error.message);
    } finally {
      console.log("ðŸ‘ completed");
      setisLoading(false);
    }
  };

  const getProduct = async (id) => {
    const response = await mockApi.get(`/product/${id}`);
    console.log("âœ… loaded products successfully");
    console.log("âœ… response", response);
  };
  return (
    <div className="App">
      <h1>Product List</h1>
      <button onClick={getProduct} disabled={isloading}>
        {isloading ? "loading" : "load products"}
      </button>

      <div
        style={{
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          marginTop: 20,
        }}
      >
        <BeatLoader
          color="cyan"
          loading={isloading}
          size="10"
          speedMultiplier={2}
        />
      </div>
    </div>
  );
}

export default App;
