import mockApi from "./api/mockApi";
import "./App.css";
import { useState } from "react";
import { BeatLoader } from "react-spinners";

function App() {
  // const getProductsThen = () => {
  //   // this is getting the base URL and adding /product behind
  //   // "mockApi.get("/product")" will get a promise, which is a future data
  //   //".then" will consume the promise to get the response data
  //   mockApi
  //     .get("/product")
  //     .then((response) => {
  //       console.log("‚úÖ loaded products successfully");
  //       console.log("‚úÖ response", response);
  //     })
  //     // axios will throw errors depending on the response of the server
  //     .catch((error) => {
  //       console.log("‚ùå error", error);
  //     })
  //     // .finally will be run be it successful or error
  //     .finally(() => {
  //       console.log("üëç completed");
  //     });
  // };

  // set a state to tell user is the app is loading or not
  const [isloading, setisLoading] = useState(false);

  const getProducts = async () => {
    // use await to consume promise
    // await can only be use at top level and with async
    // use try-catch-finally block to catch the errors. if there's error in try block, it will jump straight to catch error block, the remaining code in try block will never run
    // CRUD (read all)
    try {
      setisLoading(true);
      const response = await mockApi.get("/product");
      console.log("‚úÖ loaded products successfully");
      console.log("‚úÖ response", response);
    } catch (error) {
      console.log("‚ùå error", error.message);
    } finally {
      console.log("üëç completed");
      setisLoading(false);
    }
  };

  // CRUD (read one)
  const getProduct = async (id) => {
    try {
      setisLoading(true);
      const response = await mockApi.get(`/product/${id}`);
      console.log("‚úÖ loaded one product successfully");
      console.log("‚úÖ response", response);
    } catch (error) {
      console.log("‚ùå error", error.message);
    } finally {
      console.log("üëç completed");
      setisLoading(false);
    }
  };

  // CRUD (create)
  //database will automatically generate id
  const addProduct = async () => {
    const newProduct = {
      name: "Samsung fold",
      price: 1200,
      quantity: 1,
    };

    try {
      setisLoading(true);
      const response = await mockApi.post("/product", newProduct);
      console.log("‚úÖ loaded one product successfully");
      console.log("‚úÖ response", response);
    } catch (error) {
      console.log("‚ùå error", error.message);
    } finally {
      console.log("üëç completed");
      setisLoading(false);
    }
  };

  // CRUD (Update)

  const updateProduct = async (id) => {
    const updatedProduct = {
      name: "Iphone 16",
      price: 1000,
      quantity: 10,
    };

    // CRUD (Delete)

    try {
      setisLoading(true);
      const response = await mockApi.put(`/product/${id}`, updatedProduct);
      console.log("‚úÖ updated product successfully");
      console.log("‚úÖ response", response);
    } catch (error) {
      console.log("‚ùå error", error.message);
    } finally {
      console.log("üëç completed");
      setisLoading(false);
    }
  };

  return (
    <div className="App">
      <h1>Product List</h1>
      <button onClick={getProducts} disabled={isloading}>
        {isloading ? "loading" : "load products"}
      </button>

      <button onClick={() => getProduct(222)} disabled={isloading}>
        {isloading ? "loading one product" : "load one product"}
      </button>

      <button onClick={addProduct} disabled={isloading}>
        {isloading ? "adding product" : "add product"}
      </button>

      <button onClick={() => updateProduct(252)} disabled={isloading}>
        {isloading ? "updating product" : "update product"}
      </button>

      <div
        style={{
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          marginTop: 20,
        }}
      >
        <BeatLoader
          color="cyan"
          loading={isloading}
          size="10px"
          speedMultiplier={2}
        />
      </div>
    </div>
  );
}

export default App;
