import { useState, useEffect } from "react";

import { PropagateLoader } from "react-spinners";

import quoteApi from "./api/quoteApi";
import mockApi from "./api/mockApi";
import "./App.css";
import Table from "./components/Table";
import AddForm from "./components/AddForm";
import GovApi from "./api/GovAPI";

function App() {
  const [isLoading, setIsLoading] = useState(false);
  const [data, setData] = useState("");

  const getData = async () => {
    try {
      setIsLoading(true);
      const id = "d_c00cbf056265738b684e63e890a113d2";
      const response = await GovApi.get(`${id}/poll-download`);
      // setData((prevData)=>({...prevData, response.data}))
      setData((prev) => ({ ...prev, data: response.data }));
      console.log("âœ… loaded data successfully");
      console.log("response", data);
      // setData(`${response.data.quote} - ${response.data.author}`);
    } catch (error) {
      console.error("ðŸš¨ error: ", error.message);
    } finally {
      console.log("ðŸŽ‰ completed");
      setIsLoading(false);
    }
  };

  useEffect(() => {
    getData();
  }, []);

  return (
    <div className="App">
      <h1>Dengue Clusters List</h1>
      <button onClick={getData} disabled={isLoading}>
        {isLoading ? "Loading..." : "Load Dengue Data"}
      </button>
    </div>
  );
}

export default App;
