import mockApi from "./api/mockApi";
import "./App.css";
import { useState } from "react";

function App() {
  // const getProductsThen = () => {
  //   // this is getting the base URL and adding /product behind
  //   // "mockApi.get("/product")" will get a promise, which is a future data
  //   //".then" will consume the promise to get the response data
  //   mockApi
  //     .get("/product")
  //     .then((response) => {
  //       console.log("‚úÖ loaded products successfully");
  //       console.log("‚úÖ response", response);
  //     })
  //     // axios will throw errors depending on the response of the server
  //     .catch((error) => {
  //       console.log("‚ùå error", error);
  //     })
  //     // .finally will be run be it successful or error
  //     .finally(() => {
  //       console.log("üëç completed");
  //     });
  // };

  // set a state to tell user is the app is loading or not
  const [isloading, setisLoading] = useState(false);

  const getProducts = async () => {
    // use await to consume promise
    // await can only be use at top level and with async
    // use try-catch-finally block to catch the errors. if there's error in try block, it will jump straight to catch error block
    try {
      setisLoading(true);
      const response = await mockApi.get("/product");
      console.log("‚úÖ loaded products successfully");
      console.log("‚úÖ response", response);
    } catch (error) {
      console.log("‚ùå error", error.message);
    } finally {
      console.log("üëç completed");
      setisLoading(false);
    }
  };
  return (
    <div className="App">
      <h1>Product List</h1>
      <button onClick={getProducts}>get products</button>
    </div>
  );
}

export default App;
