import { useState, useEffect } from "react";

import { PropagateLoader } from "react-spinners";

import quoteApi from "./api/quoteApi";
import mockApi from "./api/mockApi";
import "./App.css";
import Table from "./components/Table";
import AddForm from "./components/AddForm";
import GovApi from "./api/GovAPI";

function App() {
  const [isLoading, setIsLoading] = useState(false);
  const [data, setData] = useState("");

  const getData = async () => {
    try {
      setIsLoading(true);
      const id = "d_c00cbf056265738b684e63e890a113d2";
      const response = await GovApi.get(`${id}/poll-download`);
      console.log("✅ loaded data successfully");
      console.log("response", response);
      // setData(`${response.data.quote} - ${response.data.author}`);
    } catch (error) {
      console.error("🚨 error: ", error.message);
    } finally {
      console.log("🎉 completed");
      setIsLoading(false);
    }
  };

  const getProducts = async () => {
    try {
      setIsLoading(true);
      const response = await mockApi.get("/product");
      // if error, the code below will never run (in the try block)
      console.log("✅ loaded products successfully");
      console.log("response", response);
      setProducts(response.data);
    } catch (error) {
      console.error("🚨 error: ", error.message);
    } finally {
      console.log("🎉 completed");
      setIsLoading(false);
    }
  };

  // Read One
  const getProduct = async (id) => {
    try {
      setIsLoading(true);
      // GET https://62ba9b04573ca8f8328762ca.mockapi.io/product/123
      const response = await mockApi.get(`/product/${id}`);
      console.log("response", response);
    } catch (error) {
      console.error("🚨 error: ", error.message);
    } finally {
      console.log("🎉 completed");
      setIsLoading(false);
    }
  };

  // Create
  const addProduct = async (newProduct) => {
    // const newProduct = {
    //   name: "iPhone 16 Pro Max",
    //   price: 1899,
    //   quantity: 1,
    // };

    try {
      setIsLoading(true);
      const response = await mockApi.post("/product", newProduct);
      console.log("response", response);
      // getProducts();
      setRefreshKey((prevKey) => prevKey + 1);
    } catch (error) {
      console.error("🚨 error: ", error.message);
    } finally {
      console.log("🎉 completed");
      setIsLoading(false);
    }
  };

  // Update
  const updateProduct = async (id) => {
    const updatedProduct = {
      name: "iPhone 16 Pro Max",
      price: 899,
      quantity: 2,
    };

    try {
      setIsLoading(true);
      // PUT https://62ba9b04573ca8f8328762ca.mockapi.io/product/123
      const response = await mockApi.put(`/product/${id}`, updatedProduct);
      console.log("response", response);
    } catch (error) {
      console.error("🚨 error: ", error.message);
    } finally {
      console.log("🎉 completed");
      setIsLoading(false);
    }
  };

  // Delete
  const deleteProduct = async (id) => {
    try {
      setIsLoading(true);
      const response = await mockApi.delete(`/product/${id}`);
      console.log("response", response);
    } catch (error) {
      console.error("🚨 error: ", error.message);
    } finally {
      console.log("🎉 completed");
      setIsLoading(false);
    }
  };

  return (
    <div className="App">
      <p>{quote} </p>

      <h1>Product List</h1>
      <button onClick={getProducts} disabled={isLoading}>
        {isLoading ? "Loading..." : "Load Products"}
      </button>
      <button onClick={() => getProduct(222)}>Load One Product</button>
      <button onClick={addProduct}>Add Product</button>
      <button onClick={() => updateProduct(233)}>Update Product</button>
      <button onClick={() => deleteProduct(233)}>Delete Product</button>
      <button onClick={() => setRefreshKey((prevKey) => prevKey + 1)}>
        Refresh
      </button>
      {/* {isLoading && <p>⏳ Loading ...</p>} */}
      <div style={{ marginTop: 20 }}>
        <PropagateLoader color="#36d7b7" loading={isLoading} />
      </div>
      <AddForm handlerAddItem={addProduct} />

      <Table list={products} />
    </div>
  );
}

export default App;
