import axios from "axios";

const BASE_URL = "https://dummyjson.com/quotes";

const headers = {
  "Content-Type": "application.json",
};
// creates a new instance of axios with a custom config
const quoteApi = axios.create({
  // is a prepended to all requests that are made with this instance.
  // it will put it baseURL at the front of the API call
  baseURL: BASE_URL,
  // can also configure headers, timeout, and other config options
  headers: headers,
});

export default quoteApi;
