import { useState, useEffect } from "react";
import { PropagateLoader } from "react-spinners";
import "./App.css";
import Table from "./components/Table";
import AddForm from "./components/AddForm";
import GovApi from "./api/GovAPI";
import axios from "axios";

function App() {
  const [isLoading, setIsLoading] = useState(false);
  const [data, setData] = useState([]);

  const getData = async () => {
    try {
      setIsLoading(true);
      const datasetId = "d_dbfabf16158d1b0e1c420627c0819168";
      const response = await GovApi.get(`${datasetId}/poll-download`);
      const jsonData = response.data;
      if (jsonData.code !== 0) {
        throw new Error(jsonData.errorMsg);
      }
      const fetchUrl = jsonData.data.url;
      const innerResponse = await axios.get(fetchUrl, { responseType: "text" });
      setData(innerResponse.data.features);
      // console.log(innerResponse.data.features);
      console.log("âœ… loaded data successfully");
    } catch (error) {
      console.error("ðŸš¨ error: ", error.message);
    } finally {
      console.log("ðŸŽ‰ completed");
      setIsLoading(false);
    }
  };

  useEffect(() => {
    getData();
  }, []);

  // useEffect(() => {
  //   if (data) {
  //     console.log("Updated data:", data); // This will log the updated state
  //   }
  // }, [data]);

  const CoorArray = data.map((item) => item.geometry.coordinates);

  console.log(CoorArray);
  return (
    <div className="App">
      <h1>Dengue Clusters List</h1>
      <button onClick={getData} disabled={isLoading}>
        {isLoading ? "Loading..." : "Load Dengue Data"}
      </button>
      {/* <ul>
        {data.map((item) => (
          {item.geometry.coordinates}
        ))}
      </ul> */}
    </div>
  );
}

export default App;
