import { useState, useEffect } from "react";

import { PropagateLoader } from "react-spinners";

import quoteApi from "./api/quoteApi";
import mockApi from "./api/mockApi";
import "./App.css";
import Table from "./components/Table";

function App() {
  const [isLoading, setIsLoading] = useState(false);
  const [products, setProducts] = useState([]);

  const [refreshKey, setRefreshKey] = useState(0);

  // Load data from an external system (API server) when component loads
  // useEffect hook is used synch our component state with an external system
  useEffect(() => {
    // console.log("effect running");
    getProducts();
  }, [refreshKey]);
  // dependency array
  // empty []: run once only after component mounts
  // useEffect(()=>{}, [])
  // not provided: run after every render
  // useEffect(()=>{})
  // [dependencies]: run if the dependency changed
  // useEffect(()=>{},[dependencies])

  // Example: you get param id from React Router or when id changes
  // useEffect(()=>{
  //   getProduct(id);
  // },[id])

  // const getProductsThen = () => {
  //   // https://62ba9b04573ca8f8328762ca.mockapi.io /product
  //   // .then method consumes the promise
  //   mockApi
  //     .get("/product")
  //     .then((response) => {
  //       console.log("‚úÖ loaded products successfully");
  //       console.log("response", response);
  //     })
  //     .catch((error) => {
  //       console.error("üö® error: ", error);
  //     })
  //     .finally(() => {
  //       console.log("üéâ completed");
  //     });
  // };

  // CRUD
  // Read All

  const getQuotes = async () => {
    try {
      setIsLoading(true);
      const response = await quoteApi.get("./quotes");
      console.log("‚úÖ loaded quotes successfully");
      console.log("response", response);
    } catch (error) {
      console.error("üö® error: ", error.message);
      console.error("üö® error: ", error.message);
    } finally {
      console.log("üéâ completed");
      setIsLoading(false);
    }
  };

  const getProducts = async () => {
    try {
      setIsLoading(true);
      const response = await mockApi.get("/product");
      // if error, the code below will never run (in the try block)
      console.log("‚úÖ loaded products successfully");
      console.log("response", response);
      setProducts(response.data);
    } catch (error) {
      console.error("üö® error: ", error.message);
    } finally {
      console.log("üéâ completed");
      setIsLoading(false);
    }
  };

  // Read One
  const getProduct = async (id) => {
    try {
      setIsLoading(true);
      // GET https://62ba9b04573ca8f8328762ca.mockapi.io/product/123
      const response = await mockApi.get(`/product/${id}`);
      console.log("response", response);
    } catch (error) {
      console.error("üö® error: ", error.message);
    } finally {
      console.log("üéâ completed");
      setIsLoading(false);
    }
  };

  // Create
  const addProduct = async () => {
    const newProduct = {
      name: "iPhone 16 Pro Max",
      price: 1899,
      quantity: 1,
    };

    try {
      setIsLoading(true);
      const response = await mockApi.post("/product", newProduct);
      console.log("response", response);
      // getProducts();
      setRefreshKey((prevKey) => prevKey + 1);
    } catch (error) {
      console.error("üö® error: ", error.message);
    } finally {
      console.log("üéâ completed");
      setIsLoading(false);
    }
  };

  // Update
  const updateProduct = async (id) => {
    const updatedProduct = {
      name: "iPhone 16 Pro Max",
      price: 899,
      quantity: 2,
    };

    try {
      setIsLoading(true);
      // PUT https://62ba9b04573ca8f8328762ca.mockapi.io/product/123
      const response = await mockApi.put(`/product/${id}`, updatedProduct);
      console.log("response", response);
    } catch (error) {
      console.error("üö® error: ", error.message);
    } finally {
      console.log("üéâ completed");
      setIsLoading(false);
    }
  };

  // Delete
  const deleteProduct = async (id) => {
    try {
      setIsLoading(true);
      const response = await mockApi.delete(`/product/${id}`);
      console.log("response", response);
    } catch (error) {
      console.error("üö® error: ", error.message);
    } finally {
      console.log("üéâ completed");
      setIsLoading(false);
    }
  };

  return (
    <div className="App">
      <h1>Product List</h1>
      <button onClick={getProducts} disabled={isLoading}>
        {isLoading ? "Loading..." : "Load Products"}
      </button>
      <button onClick={() => getProduct(222)}>Load One Product</button>
      <button onClick={addProduct}>Add Product</button>
      <button onClick={() => updateProduct(233)}>Update Product</button>
      <button onClick={() => deleteProduct(233)}>Delete Product</button>
      <button onClick={() => setRefreshKey((prevKey) => prevKey + 1)}>
        Refresh
      </button>
      {/* {isLoading && <p>‚è≥ Loading ...</p>} */}
      <div style={{ marginTop: 20 }}>
        <PropagateLoader color="#36d7b7" loading={isLoading} />
      </div>

      <Table list={products} />
    </div>
  );
}

export default App;
